package com.xworkz.programme.boots;

public class DecreaseingPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("creating the decreasing pattern");
		System.out.println(System.lineSeparator());
		for (int i = 0; i <= 4; i++) {
			for (int j = 0; j <= i; j++) {

				
					System.out.print("*");
				
				}
			System.out.println(" ");

			}
			
		}
	}


