public class IncreasingPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("creating the decreasing pattern");
		System.out.println(System.lineSeparator());
		for (int i = 0; i <= 4; i++) {
			for (int j = i; j <= 4; j++) {

				
					System.out.print("*");
				
				}
			System.out.println(" ");

			}
	}

}
